import xml.etree.ElementTree as ET
import re

def parse_xml_file(xml_file_path, page_contains_one_of, page_must_contain_all=None, section_split=None):
    if page_contains_one_of is not None and not isinstance(page_contains_one_of, list):
        raise TypeError("page_contains_one_of must be a list")

    # Parsing the XML file
    xml_tree = ET.parse(xml_file_path)
    root_element = xml_tree.getroot()

    # Iterate over 'page' elements to find the content by URL
    for index, page_element in enumerate(root_element.findall('{http://www.mediawiki.org/xml/export-0.11/}page')):
        # Find the 'title' and 'text' elements under each 'page' element
        page_title = page_element.find('{http://www.mediawiki.org/xml/export-0.11/}title').text
        page_text = page_element.find('{http://www.mediawiki.org/xml/export-0.11/}revision').find('{http://www.mediawiki.org/xml/export-0.11/}text').text

        continue_flag = True

        if page_contains_one_of is not None:
          for term in page_contains_one_of:
            if term in page_text:
              continue_flag = False
              break

        if continue_flag:
          continue


        if page_must_contain_all is not None:
          for must_term in page_must_contain_all:
              if must_term not in page_text:
                  continue_flag = True
                  break

        if continue_flag:
            continue

        if section_split is not None:
            section = extract_section_from_content(page_text, section_split)
            if section is not None:
                yield page_title, section, term
            else:
                continue
        else:
            yield page_title, ET.tostring(page_element, encoding='utf8').decode('utf8'), term


def extract_section_from_content(content, section_split):
    if section_split is not None:
        if len(section_split) == 1:
            section_title = section_split[0]
        elif len(section_split) == 2:
            section_title, stop_at = section_split
        else:
            raise ValueError("section_split must be a list of length 1 or 2")

    # sub-sample the content to the section of interest
    start_index = content.find(section_title)
    if start_index == -1:
        return None
    content = content[start_index:]
    if stop_at is not None:
        end_index = content.find(stop_at)
        if end_index == -1:
            return None
        content = content[:end_index]

    return content


def clean_eligibility_criteria(text):
    # text = text.split("== מי זכאי? ==  \n", 1)[-1]
    # Remove text enclosed in [[ ]]
    text = re.sub(r'\[\[.*?\]\]', '', text)
    # Remove URLs enclosed in [https:// ]
    text = re.sub(r'\[https://.*?\]', '', text)
    return text



